import React, { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';




const app = new Clarifai.App({
  apiKey: 'd5dce337ce2f404fad71e20b1fcdf405'
});

const particlesOptions = {
    particles: {
      number: {
       value:100,
       density:{
         enable: true,
         value_area:900,
        size:{
          value:5,
          random:true
        }
       }
    }
  }}



class App extends Component {

  constructor(){
    super();
  this.state ={
   input: '',
   imageUrl: '',
   box:{}
    }
  }

calculateFaceLocation(data){
 const clarifaiFace = data.response.output[0].data.regions[0].regions_info.bounding_box;
const image = document.getElementById('inputimage');
const width = Number(image.width);
const height = Number(image.height);
return {
  leftCol: clarifaiFace.left_col * width,
  topRow: clarifaiFace.top_row * height,
  rightCol: width - (clarifaiFace.right_col * width),
  bottonRom: height -(clarifaiFace.bottom_row * height)
}
}
displaFaceBox =(box)=>{
this.setState({box: box});
}

  onInputChange =(event) => {
    console.log(event.target.value);
  this.setState({input:event.target.value})
  }


  onSubmitButton= ()=>{
    this.setState({imageUrl:this.state.input})

    app.models
    .predict(Clarifai.FACE_DETECT_MODEL, 
     this.state.input)
    .then(response=>this.displaFaceBox(this.calculateFaceLocation(response)))
  .catch(err=> console.log(err,'Oooooops try again'))
  }

  render() {
    return (
      <div className="App">
        <Particles className='particles'
        params={particlesOptions} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
        onInputChange={this.onInputChange} 
        onSubmitButton={this.onSubmitButton} />
         <FaceRecognition imageUrl={this.state.imageUrl}/>
      </div>
    );
  }

}

export default App;
